import { combineReducers } from 'redux';
import { restaurants } from '../App';
const INITIAL_CART = {
    cart: [],
    restaurant: ''
}

export const addPrato = prato => ({
    'type' : 'addPrato',
    'payload' : prato
});

export const delPrato = prato => ({
    'type' : 'delPrato',
    'payload' : prato
});

function cartReducer(state = INITIAL_CART, action)   {
    switch(action.type) {
        case 'addPrato':
            let restauranteIn = '';
            for( let restaurant in restaurants ) for( let prato in restaurant.Pratos ) if (prato === action.payload) restauranteIn = restaurant.name;
            if(state.restaurant != '') {
                if(state.restaurant != restauranteIn) return; 
            }
            return{...state,
            cart: state.cart.concat(action.payload),
            restaurant: state.restaurant.substring(0,0).concat(restauranteIn)}
        case 'delPrato':
            let restauranteOut = state.restaurant;
            if(state.cart.length == 1) restauranteOut = '';
            return{...state,
            cart: state.cart.filter(prato => prato !== action.payload),
            restaurant: restauranteOut}
        default:
            return state;
    }
}

export default combineReducers({
    cartReducer: cartReducer
  });
