import React, {useState} from 'react';
import {StyleSheet, Text, View, ScrollView, Image, TouchableWithoutFeedback} from 'react-native';

export default function Cart() {
const [cart, setCart] = useState([]);
console.log(cart);
    return(
        <View>
            <Text style={styles.restaurantesOffer}>Pratos Disponíveis:</Text>
            <ScrollView>{
                cart.map(prato => { return(
                    <TouchableWithoutFeedback key={cart.id} onPress={setCart(cart - prato)}>
                        <View style={styles.containerRow}>
                        <Image style={styles.image} source={cart.image}/>
                        <View style={styles.containerColumn}>
                            <Text style={styles.restaurantesOffer}>{prato}</Text>
                            <Text style={styles.foodText}>Opções : {cart.Opçoes}
                            {"\n"}
                            Preço : {cart.Preço} €</Text>
                        </View>
                        </View> 
                    </TouchableWithoutFeedback>
                );
                })   
            }</ScrollView>
        </View>
        
    );
}
const styles = StyleSheet.create({
    containerRow: {
      paddingBottom: 10,
      flexDirection: 'row'
    },
    containerColumn: {
        paddingLeft: 10,
        paddingBottom: 10,
        flexDirection: 'column'
    },
    restaurantesOffer: {
        fontSize: 15,
        fontWeight: 'bold',
        textAlign: 'left',
        color: 'black'
    },
    foodText: {
        fontSize: 12,
        textAlign: 'left',
        
    },
    image: {
        width: 150,
        height: 100,
    }
});
