import React from 'react';
import {StyleSheet, Text, View, ScrollView, Image, TouchableWithoutFeedback} from 'react-native';
import { restaurants } from '../App';
export default  function Home() {
    return (
        <View style={{flexDirection: 'column', flex: 1, padding:10}}>
        <Text style={styles.restaurantesTitle}>
            Escolha o seu restaurante preferido entra a nossa seleção!
        </Text>
        <ScrollView style={{flexDirection: 'row', flex: 1}} horizontal>{
            Object.keys(restaurants).map((restaurante) => {return(
                <TouchableWithoutFeedback  key={restaurants[restaurante].id}  onPress={() => navigation.push('cart', {restaurante : restaurante})}>
                <View key={restaurants[restaurante].id} style={styles.restaurantesPromo}>{
                    <View style={{flexDirection: 'column'}}>
                        <Image source={restaurants[restaurante].image} style={styles.image}/> 
                        <Text style={styles.restaurantesTitle}>{restaurante}</Text>
                    </View>
                }</View>
                </TouchableWithoutFeedback>
            )})
        }</ScrollView>
        </View>

    );
}


const styles = StyleSheet.create({
    restaurantesPromo : {
        flexDirection: 'column',
        backgroundColor: 'ghostwhite',
        padding: 5
    },
    image: {
        width: 150,
        height: 100,
    },
    restaurantesTitle: {
        fontSize: 15,
        fontWeight: 'bold',
        textAlign: 'center',

      },
});
