import React from 'react';
import { createBottomTabNavigator } from 'react-navigation-tabs';
import restaurantes from './screens/Restaurantes';
import home from './screens/Home'
import { createStackNavigator, createAppContainer } from 'react-navigation';
var imgMcDonalds = require ('./assets/mcdonalds.jpg');
var imgTelepizza = require ('./assets/telepizza.jpg');

export const restaurants = {
  "McDonald's" : {
    "id" : 0,
    "PricePoint" : "€",
    "Tags" : ["Hambúrgueres","Gelado e iogurte gelado", "Sobremesas","Adequado para crianças","Americana" ,"Fast food"],
    "Pratos" : {
      "McMenu BigMac LARGE" : { "Preço" : 2, "Opçoes" : ["Extra queijo", "Extra DIP"] }
    },
    "image" : imgMcDonalds,
  },
  "Telepizza" : {
    "id" : 1,
    "Address" : "R. De José Rabumba, 3810-164 Aveiro, Portugal, 7, Aveiro 3810-164",
    "PricePoint" : "€",
    "Tags" : ["Pizza", "Fast Food"],
    "Pratos" : {
      "Pizza Havaiana" : { "Preço" : 20, "Opçoes" : ["Extra queijo"] },
    },
    "image" : imgTelepizza,
  },
}

const TabNav = createBottomTabNavigator({
  home, restaurantes
});
export default createAppContainer(TabNav);
