import React, { useState } from 'react';
import { TextInput, StyleSheet, View, Text, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import ClienteApp from './screens/AppCliente/AppClientes';


const users = {
    "cliente": {
        "type": "cliente",
        "password": "cliente",
    },
    "restaurante": {
        "type": "restaurante",
        "password": "restaurante",
    },
    "estafeta": {
        "type": "estafeta",
        "password": "estafeta",
    },
}

const Tab = createStackNavigator();

export default function AppContent() {
    return (
        <NavigationContainer independent={true}>
            <Tab.Navigator initialRouteName='Home' headerMode='none'>
                <Tab.Screen name="Home" component={Login} />
                <Tab.Screen name="AppCliente" component={ClienteApp}/>
            </Tab.Navigator>
        </NavigationContainer>);
}


function Login({ navigation }) {
    const [user, setUser] = useState("");
    const [password, setPassword] = useState("");
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Nutrilink</Text>
            <View style={styles.loginContainer}>
                <View style={styles.inputView}>
                    <TextInput placeholder='Username' style={styles.input} onChangeText={username => setUser(username)} />
                </View>
                <View style={styles.inputView}>
                    <TextInput placeholder='Password' style={styles.input} onChangeText={pass => setPassword(pass)} secureTextEntry />
                </View>
                <TouchableOpacity style={styles.loginButtonContainer} onPress={() => {
                    for (let typeUser of Object.keys(users))
                        if (user === typeUser) {
                            switch (users[user].type) {
                                case "cliente":
                                    navigation.push("AppCliente");
                                    break;
                                case "restaurante":
                                    navigation.push("AppRstaurante");
                                    break;
                                case "estafeta":
                                    navigation.push("AppEstafeta");
                                    break;
                                default: 
                                    break;
                            }
                        }
                }}>
                    <Text style={styles.loginButtonText}>Login</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const goToApp = (navigation, user, password,) => {
    console.log(navigation)

}

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'darkcyan',
        alignItems: 'center',
        flex: 1

    },
    loginContainer: {
        backgroundColor: 'darkcyan',
        alignItems: 'center',
        justifyContent: 'center',
        flex: 1
    },
    title: {
        borderTopWidth: 100,
        borderTopColor: 'darkcyan',
        fontSize: 40,
        fontWeight: 'bold',
        color: 'cornsilk'
    },
    inputView: {
        borderBottomWidth: 10,
        borderBottomColor: 'darkcyan'
    },
    input: {
        backgroundColor: 'darkgray',
        padding: 15,
        borderRadius: 5,
    },
    loginButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
    },
    loginButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
    }
});
