import React, { useState } from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Cart from './screens/Cart';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { connect } from 'react-redux';
import Icon from 'react-native-vector-icons/Ionicons';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import cartReducer from './assets/cartState';
import restauranteMenu from './screens/RestauranteMenu';
import restaurantes from './screens/Restaurantes';
import home from './screens/Home';
var imgVegifruit = require('./assets/vegifruit.png');
var imgGreenCity = require('./assets/greencity.png');
var imgSaladasmais = require('./assets/saladasmais.jpg');
var imgQuichVeg = require('./assets/quicheVegsGluten.jpg');
var imgCremeEspi = require('./assets/cremedeespinafres.jpg');
var imgSaladaQSerra = require('./assets/saladaqueijoserra.jpg');
var imgSandesBase = require('./assets/sandesBase.jpg');
var imgPratoCarne = require('./assets/pratoCarne.jpg');
var imgPratoPeixe = require('./assets/pratoPeixe.jpg');


export const restaurants = {
  "Vegifruit": {
    "name": "Vegifruit",
    "id": 0,
    "PricePoint": "€",
    "Address": "Rua de São Sebastião, 122 3810 - 187 Aveiro",
    "Tags": ["Vegan", "Saladas", "Biologico"],
    "Pratos": {
      "Salada de queijo da serra": {
        "name": "Salada de queijo da serra",
        "id": 0,
        "Preço": 7,
        "Opçoes": ["Extra azeitonas"],
        "image": imgSaladaQSerra
      },
      "Quiche Vegetariana s/Glúten": {
        "name": "Quiche Vegetariana s/Glúten",
        "id": 1,
        "Preço": 6.5,
        "Opções": [],
        "image": imgQuichVeg,
      },
      "Creme de espinafres": {
        "name": "Creme de espinafres",
        "id": 2,
        "Preço": 6.5,
        "Opções": [],
        "image": imgCremeEspi,
      },

    },
    "image": imgVegifruit,
  },
  "Green City quiches & saladas, co.": {
    "name": "Green City quiches & saladas, co.",
    "id": 1,
    "PricePoint": "€",
    "Address": "R. de Castro Matoso 28 A, 3810-079 Aveiro",
    "Tags": ["Vegan", "Saladas", "Quiches", "Biologico"],
    "Pratos": {
      "Quiche especial": {
        "name": "Quiche especial",
        "id": 0,
        "Preço": 6,
        "Opçoes": ["Tamanho grande"],
        "image": imgGreenCity
      }
    },
    "image": imgGreenCity,
  },
  "Saladas+": {
    "name": "Saladas+",
    "id": 2,
    "PricePoint": "€",
    "Address": "R. de São Sebastião 31, 3810-187 Aveiro",
    "Tags": ["Vegan", "Saladas", , "Biologico"],
    "Pratos": {
      "Sandes Base": {
        "name": "Sandes Base",
        "id": 0,
        "Preço": 3.10,
        "Opçoes": ["Extra ovo"],
        "image": imgSandesBase
      },

      "Prato do dia Peixe": {
        "name": "Prato do dia Peixe",
        "id": 1,
        "Preço": 7.45,
        "Opçoes": ["Extra tempero"],
        "image": imgPratoPeixe
      },

      "Prato do dia Carne": {
        "name": "Prato do dia Carne",
        "id": 2,
        "Preço": 7.45,
        "Opçoes": ["Extra acompanhamento"],
        "image": imgPratoCarne
      }
    },
    "image": imgSaladasmais,
  },
}

const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer independent={true}>
      <Tab.Navigator initialRouteName='Home' screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === 'Home') {
            iconName = "ios-home";
          } else if (route.name === 'Restaurantes') {
            iconName = "ios-restaurant";
          }
          else iconName = "md-cart";
          // You can return any component that you like here!
          return <Icon name={iconName} size={size} color={color} />;
        },
      })}
        tabBarOptions={{
          activeTintColor: 'tomato',
          inactiveTintColor: 'gray',
        }}>
        <Tab.Screen name="Restaurantes" component={CartStack} />
        <Tab.Screen name="Home" component={HomeStack} />
        <Tab.Screen name="Carrinho" component={Cart} />
      </Tab.Navigator>
    </NavigationContainer>);
}

const CartStackNav = createStackNavigator();

function CartStack(route) {
  let restaurante = '';
  console.log(route)
  if (route.params)
    restaurante = route.params.restaurante;
  return (
    <CartStackNav.Navigator initialroute={'Restaurantes'} >
      <CartStackNav.Screen name="Restaurantes" component={restaurantes} />
      <CartStackNav.Screen name="Pratos" component={restauranteMenu} initialParams={{ restaurante: restaurante }} />
    </CartStackNav.Navigator>

  );
}

const Stack = createStackNavigator();
function HomeStack(route) {
  console.log(route)
  let restaurante = '';
  if (route.params)
    restaurante = route.params.restaurante;
  return (
    <Stack.Navigator initialroute={'Home'} >
      <Stack.Screen name="Home" component={home} />
      <Stack.Screen name="Pratos" component={restauranteMenu} initialParams={{ restaurante }} />
    </Stack.Navigator>


  );
}

const store = createStore(cartReducer);

const mapStateToProps = (state) => {
  return state;
};

connect(mapStateToProps)(() => {
  return (
    <Provider store={store}>
      <App />
    </Provider>
  );
});
