import React, {useState, useEffect} from 'react';
import {StyleSheet, Text, View, ScrollView, Image, TouchableWithoutFeedback} from 'react-native';
//import cart, { setCart } from '../App';
//onPress={setCart(cart + restaurante.Pratos[prato])}

export default function RestauranteMenu({navigation, route}){
    //<Image source={restaurante.image} style={styles.image}/> 
    var restaurante = route.params.restaurante;
    //else restaurante = navigation.state.params.restaurante;
    return(
        <View>
            <Text style={styles.restaurantesOffer}>Pratos Disponíveis:</Text>
            <ScrollView>{
                Object.keys(restaurante.Pratos).map(prato => { return(
                    <TouchableWithoutFeedback key={restaurante.Pratos[prato].id} onPress={cart.append(prato)}>
                        <View style={styles.containerRow}>
                        <Image style={styles.image} source={restaurante.Pratos[prato].image}/>
                        <View style={styles.containerColumn}>
                            <Text style={styles.restaurantesOffer}>{prato}</Text>
                            <Text style={styles.foodText}>Opções : {restaurante.Pratos[prato].Opçoes}
                            {"\n"}
                            Preço : {restaurante.Pratos[prato].Preço} €</Text>
                        </View>
                        </View> 
                    </TouchableWithoutFeedback>
                );
                })   
            }</ScrollView>
        </View>

    );
}



const styles = StyleSheet.create({
    containerRow: {
      paddingBottom: 10,
      flexDirection: 'row'
    },
    containerColumn: {
        paddingLeft: 10,
        paddingBottom: 10,
        flexDirection: 'column'
    },
    restaurantesOffer: {
        fontSize: 15,
        fontWeight: 'bold',
        textAlign: 'left',
        color: 'black'
    },
    foodText: {
        fontSize: 12,
        textAlign: 'left',

    },
    image: {
        width: 150,
        height: 100,
    }
});
