import React, { useState } from 'react';
import { StyleSheet, Text, View, ScrollView, Image, TouchableWithoutFeedback } from 'react-native';

var imgQuichVeg = require('../assets/quicheVegsGluten.jpg');
var imgCremeEspi = require('../assets/cremedeespinafres.jpg');
var imgSaladaQSerra = require('../assets/saladaqueijoserra.jpg');
export default function Cart({ route }) {
    const getTotal = () => {
        let res = 0;
        for(let prato of cart) {
            console.log(prato)    
            res += prato.Preço;
        }
        return res;
    }
    const cart = [{
        "name": "Salada de queijo da serra",
        "id": 0,
        "Preço": 7,
        "Opçoes": ["Extra azeitonas"],
        "image": imgSaladaQSerra
      },
      {
        "name": "Quiche Vegetariana s/Glúten",
        "id": 1,
        "Preço": 6.5,
        "Opções": [],
        "image": imgQuichVeg,
      },
      {
        "name": "Creme de espinafres",
        "id": 2,
        "Preço": 6.5,
        "Opções": [],
        "image": imgCremeEspi,
      },];
    return (
        <View>
            <Text style={styles.restaurantesOffer}>Carrinho:</Text>
            <ScrollView>{
                cart.map(prato => {
                    return (
                        <TouchableWithoutFeedback key={prato.id}>
                            <View style={styles.containerRow}>
                                <Image style={styles.image} source={prato.image} />
                                <View style={styles.containerColumn}>
                                    <Text style={styles.restaurantesOffer}>{prato.name}</Text>
                                    <Text style={styles.foodText}>Opções : {prato.Opçoes}
                                        {"\n"}
                            Preço : {prato.Preço} €</Text>
                                </View>
                            </View>
                        </TouchableWithoutFeedback>
                    );
                })
            }</ScrollView>
            <Text style={styles.restaurantesOffer}>Preço total: {getTotal()}€</Text>
        </View>

    );
}
const styles = StyleSheet.create({
    containerRow: {
        paddingBottom: 10,
        flexDirection: 'row'
    },
    containerColumn: {
        paddingLeft: 10,
        paddingBottom: 10,
        flexDirection: 'column'
    },
    restaurantesOffer: {
        fontSize: 15,
        fontWeight: 'bold',
        textAlign: 'left',
        color: 'black'
    },
    foodText: {
        fontSize: 12,
        textAlign: 'left',

    },
    image: {
        width: 150,
        height: 100,
    }
});
